mapper.update:
update t_comment
set
  comment_count = comment_count + #{commentCount,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

update t_comment
set
  like_count = like_count + #{likeCount,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

update t_comment
set
  like_count = like_count - #{likeCount,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}
