mapper.select:
select appid,price from t_steam_history_price
where gmt_create>#{time}
and type=#{type}

select price,gmt_create from t_steam_history_price
where appid = #{appid}
and type = #{type}

select price,gmt_create from t_steam_history_price
where appid = #{appid}
and type = #{type}
and gmt_create>#{time}
